import {React, useState} from 'react';
import ReactDOM from 'react-dom/client';
import './style.css'

function Panel({children}){
    const [test, setPosition] = useState(() => "");

    function startDrag(e){
        window.addEventListener('mousemove', drag);
        window.addEventListener('mouseup', drop);
        window.addEventListener('mouseout', drop);
    }

    function drag(e){
        console.log("oui");
        setPosition((test) => " red");
    }

    function drop(e){
        console.log("drop");

        setPosition((test) => " ");
        window.removeEventListener('mousemove', drag);
        window.removeEventListener('mouseup', drop);
        window.removeEventListener('mouseout', drop);
    }
    return(
        <div className='panel'>
            <div style={{backgroundColor: test}} className="boxSelector" onMouseDown={startDrag}></div>
            {children}
        </div>
    )
}

function App(){
    return(
        <main>
            <Panel></Panel>
            <Panel></Panel>
            <Panel></Panel>
        </main>
    )
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<App></App>);
