import {React, useState, useRef} from 'react';
import ReactDOM from 'react-dom/client';
import './style.css'

var selectedPanel;
var diffPos;

function MovePanelStart(panel, posX){
    selectedPanel = panel;
    diffPos = posX - panel.current.getClientRects()[0].x

    selectedPanel.current.style.position = "absolute";
    selectedPanel.current.style.left = posX-diffPos+"px";

    window.addEventListener('mousemove', MovePanel);
    window.addEventListener('mouseup', MovePanelRelease);
}

function MovePanel(e){
    selectedPanel.current.style.left = e.clientX-diffPos+"px";
}

function MovePanelRelease(e){
    selectedPanel.current.style.left = e.clientX-diffPos+"px";
    selectedPanel.current.style.position = "inherit";
    window.removeEventListener('mousemove', MovePanel);
    window.removeEventListener('mouseup', drop);
}

function Panel({children}){
    const panel = useRef();


    function startDrag(e){
        //window.addEventListener('mousemove', drag);
        window.addEventListener('mouseup', drop);
        

        MovePanelStart(panel, e.clientX)

        //setDiff((diff) => e.clientX - panel.current.getClientRects()[0].x)

        //panel.current.style.position = "absolute";
        //panel.current.style.left = e.clientX-diffPos+"px";
    }

    function drag(e){
        //panel.current.style.left = e.clientX-diffPos+"px";
    }

    function drop(e){
        panel.current.style.left = e.clientX-diffPos+"px";
        panel.current.style.position = "inherit";
        window.removeEventListener('mousemove', drag);
        window.removeEventListener('mouseup', drop);
    }
    
    return(
        <div  ref={panel} className='panel'>
            <div className="boxSelector" onMouseDown={startDrag}></div>
            {children}
        </div>
    )
}

function Principal(){
    const panel1 = useRef();

    return(
        <>
            <main>
                <Panel ref={panel1}>test 1</Panel>
                <Panel>test 2</Panel>
                <Panel>test 3</Panel>
            </main>
            
        </>
    )
}

function App(){
    return(
        <Principal></Principal>
    )
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<App></App>);
