import {React, useState, useEffect, useRef, cloneElement} from 'react';
import ReactDOM from 'react-dom/client';
import './style.css'

var selectedPanel;
var diffPos;
var texts = ["test 1", "test 2", "test 3"];

function MovePanelStart(panel, posX){
    selectedPanel = panel;
    diffPos = posX - panel.current.getClientRects()[0].x

    selectedPanel.current.style.position = "absolute";
    selectedPanel.current.style.left = posX-diffPos+"px";

    window.addEventListener('mousemove', MovePanel);
    window.addEventListener('mouseup', MovePanelRelease);
    texts.push("test 4");

    console.log(cloneElement(selectedPanel));
    
}

function MovePanel(e){
    selectedPanel.current.style.left = e.clientX-diffPos+"px";
}

function MovePanelRelease(e){
    selectedPanel.current.style.left = e.clientX-diffPos+"px";
    selectedPanel.current.style.position = "inherit";
    window.removeEventListener('mousemove', MovePanel);
    window.removeEventListener('mouseup', MovePanelRelease);
}

function Panel({setListPanel, children}){
    const panel = useRef();

    function startDrag(e){
        MovePanelStart(panel, e.clientX)
        setListPanel
    }
    
    return(
        <div  ref={panel} className='panel'>
            <div className="boxSelector" onMouseDown={startDrag}></div>
            {children}
        </div>
    )
}

function Principal(){
    const [listPanel, setListPanel] = useState([]);

    console.log(listPanel)

    useEffect(()=>{
        console.log("update");
        setListPanel(texts.map((text, id) =>
            <Panel key={id} setListPanel={setListPanel}> {text}</Panel>
        ));
    }, [texts])

    return(
        <>
            <main>
                {listPanel}
            </main>
        </>
    )
}

function App(){
    return(
        <Principal></Principal>
    )
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<App></App>);
