import {React, useState} from 'react';
import ReactDOM from 'react-dom/client';
import './style.css'

function Panel({children}){
    const [pos, setPosition] = useState(() => "");
    const [disp, setDisplay] = useState(() => "block");

    function startDrag(e){
        window.addEventListener('mousemove', drag);
        window.addEventListener('mouseup', drop);
        window.addEventListener('mouseout', drop);
        setPosition((pos) => " red");
        setDisplay((disp) => "absolute");
    }

    function drag(e){
        console.log("oui");
    }

    function drop(e){
        console.log("drop");

        setPosition((pos) => " ");
        setDisplay((disp) => "block");
        window.removeEventListener('mousemove', drag);
        window.removeEventListener('mouseup', drop);
        window.removeEventListener('mouseout', drop);
    }

    
    return(
        <div style={{display: disp}} className='panel'>
            <div style={{backgroundColor: pos}} className="boxSelector" onMouseDown={startDrag}></div>
            {children}
        </div>
    )
}

function App(){
    return(
        <main>
            <Panel></Panel>
            <Panel></Panel>
            <Panel></Panel>
        </main>
    )
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<App></App>);
