function App(){
    const CopyPanel = forwardRef((props, ref) => (
        <Panel ref={ref} visibility={"hidden"}> {props.children} </Panel>
    ))

    return(
        <>
            <Principal></Principal>
            <CopyPanel></CopyPanel>
        </>
    )
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<App></App>);
