import {React, useState} from 'react';
import ReactDOM from 'react-dom/client';
import './style.css'

function Panel({children}){
    //const [testClassName, setClassName] = useState(() => "boxSelector");

    function drag(e){
        //e.preventDefault();
        console.log("drag");
        
        window.addEventListener('mouseup', (event) => {
            drop(event)
        });
    }

    function drop(e){
        //e.preventDefault();
        console.log("drop");
    }
    return(
        <div className='panel'>
            <div className="boxSelector" onMouseDown={drag} onMouseUp={drop} ></div>
            {children}
        </div>
    )
}

function App(){
    return(
        <main>
            <Panel></Panel>
            <Panel></Panel>
            <Panel></Panel>
        </main>
    )
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<App></App>);
