import {React, useState, useRef} from 'react';
import ReactDOM from 'react-dom/client';
import './style.css'

function Panel({children}){
    const panel = useRef();

    const [leftPos, setLeft] = useState(() => "0px");
    const [disp, setDisplay] = useState(() => "inherit");
    const [diffPos, setDiff] = useState(() => 0);

    function startDrag(e){
        window.addEventListener('mousemove', drag);
        window.addEventListener('mouseup', drop);
        setDiff((diff) => e.clientX - panel.current.getClientRects()[0].x)
        setDisplay((disp) => "absolute");
        setLeft((pos) => e.clientX-diffPos+"px");
    }

    function drag(e){
        setLeft((pos) => e.clientX-diffPos+"px");
    }

    function drop(e){
        setLeft((pos) => "0px");
        setDisplay((disp) => "inherit");
        window.removeEventListener('mousemove', drag);
        window.removeEventListener('mouseup', drop);
    }
    
    return(
        <div style={{position: disp, left: leftPos}} ref={panel} className='panel'>
            <div className="boxSelector" onMouseDown={startDrag}></div>
            {children}
        </div>
    )
}

function App(){
    return(
        <main>
            <Panel></Panel>
            <Panel></Panel>
            <Panel></Panel>
        </main>
    )
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<App></App>);
